# Funtions for mutual conversion among lifetable functions
lxtodx <- function(lx) {
 nc <- length(lx)
 dx <- numeric(nc)
 for (i in 1:(nc-1)) {
  dx[i] <- lx[i]-lx[i+1]
 }
 dx[nc] <- lx[nc]
 return(dx)
}

dxtolx <- function(dx) {
 nc <- length(dx)
 lx <- numeric(nc)
 lx[1] <- 100000
 for (i in 2:nc) {
  lx[i] <- lx[i-1]-dx[i-1]
 }
 lx[nc] <- 0
 return(lx)
}

qxtodx <- function(qx) {
 nc <- length(qx)
 lx <- numeric(nc)
 dx <- numeric(nc)
 lx[1] <- 100000
 for (i in 1:(nc-1)) {
  dx[i] <- lx[i]*qx[i]
  lx[i+1] <- lx[i]-dx[i]
 }
 dx[nc] <- lx[nc]*qx[nc]
 lx[nc] <- 0
 return(dx)
}

dxtoqx <- function(dx) {
 nc <- length(dx)
 lx <- numeric(nc)
 qx <- numeric(nc)
 lx[1] <- 100000
 for (i in 1:(nc-1)) {
  qx[i] <- dx[i]/lx[i]
  lx[i+1] <- lx[i]-dx[i]
 }
 qx[nc] <- 1
 return(qx)
}

qxtomx <- function(qx, ax=0.5, n=1, mmax=NULL) {
 mx <- qx/(n*(1-qx*(1-ax)))
 if (!is.null(mmax)) { mx[length(mx)] <- mmax }
 return(mx)
}

mxtoqx <- function(mx, ax=0.5, n=1) {
 qx <- n*mx/(1+n*mx*(1-ax))
 qx[length(qx)] <- 1
 return(qx)
}

qxtolx <- function(qx) {
 nc <- length(qx)
 lx <- numeric(nc)
 dx <- numeric(nc)
 lx[1] <- 100000
 for (i in 1:(nc-1)) {
  dx[i] <- lx[i]*qx[i]
  lx[i+1] <- lx[i]-dx[i]
 }
 lx[nc] <- 0
 return(lx)
}

lxtoqx <- function(lx) {
 nc <- length(lx)
 dx <- numeric(nc)
 qx <- numeric(nc)
 for (i in 2:nc) {
  dx[i-1] <- lx[i-1]-lx[i]
  qx[i-1] <- dx[i-1]/lx[i-1]
 }
 dx[nc] <- lx[nc]
 qx[nc] <- 1
 return(qx)
}

uxtoqx <- function(ux) {
 nc <- length(ux)
 lx <- numeric(nc)
 lnlx <- numeric(nc)
 dx <- numeric(nc)
 qx <- numeric(nc)
 lx[1] <- 100000
 lnlx[1] <- log(lx[1])
 for (i in 2:nc) {
  lnlx[i] <- lnlx[i-1]-ux[i-1]
  lx[i] <- exp(lnlx[i])
  dx[i-1] <- lx[i-1]-lx[i]
  qx[i-1] <- dx[i-1]/lx[i-1]
 }
 dx[nc] <- lx[nc]
 qx[nc] <- 1
 return(qx)
}

# making lifetable from mx
lifetable <- function(mx, ns=NULL, class=5, mode=1) {
 nc <- length(mx)
 qx <- numeric(nc)
 if (mode>10) { mode <- mode %% 10; grev <- TRUE } else { grev <- FALSE }
 if (is.null(ns)) {
  n <- rep(class, nc)
  ages <- c(0, cumsum(n)[1:(nc-1)])
  if (mode %in% 4:5) { mode <- mode-2 }
 }
 else {
  n <- ns
  ages <- c(0, cumsum(n)[1:(nc-1)])
  if (mode %in% 2:3) { mode <- mode+2 }
 }
 if (mode==1) { ax <- c(rep(0.5, nc-1), 1/mx[nc])
 } else if (mode==2) { ax <- c(0.1, rep(0.4, 4), rep(0.5, nc-6), 1/mx[nc])
 } else if (mode==3) { ax <- c(0.3, rep(0.4, 4), rep(0.5, nc-6), 1/mx[nc])
 } else if (mode==4) { ax <- c(0.1, 0.4, rep(0.5, nc-3), 1/mx[nc])
 } else if (mode==5) { ax <- c(0.3, 0.4, rep(0.5, nc-3), 1/mx[nc])
 } else if (mode==6) { 
   if (mx[1]<0.107) {
     ax <- c(0.045+2.684*mx[1], (1.651-2.816*mx[1])/4, rep(0.5, nc-3), 1/mx[nc])
   } else {
     ax <- c(0.330, 1.352/4, rep(0.5, nc-3), 1/mx[nc])
   } # Males
 } else if (mode==7) {
   if (mx[1]<0.107) {
     ax <- c(0.053+2.8*mx[1], (1.522-1.518*mx[1])/4, rep(0.5, nc-3), 1/mx[nc])
   } else {
     ax <- c(0.350, 1.361/4, rep(0.5, nc-3), 1/mx[nc])
   } # Females
 } else {
   ax <- c(rep(0.5, nc-1), 1/mx[nc]) # default is mode 1
 }
 if (!grev) { 
  qx <- n*mx/(1+n*(1-ax)*mx)
  qx[nc] <- 1
 } # simple calculation
 else { # Greville's correction (ignoring ax)
  for (i in 1:(nc-1)) {
   qx[i] <- mx[i] / (1/n[1]+mx[i]*(1/2+n[i]/12*(mx[i]-(log(mx[i+1])-log(mx[i]))/n[i]))) / n[i]
  }
  qx[nc] <- 1
 }
 px <- dx <- lx <- Lx <- numeric(nc)
 lx[1] <- 100000
 px <- 1-qx
 for (i in 1:(nc-1)) {
  dx[i] <- lx[i]*qx[i]
  lx[i+1] <- lx[i]-dx[i]
  Lx[i] <- n[i]*(lx[i+1]+ax[i]*dx[i])
 }
 dx[nc] <- lx[nc]
 Lx[nc] <- lx[nc]/mx[nc] # same as n[nc]*lx[nc] - n[nc]*(1-ax[nc])*dx[nc]
 Tx <- rev(cumsum(rev(Lx)))
 ex <- Tx/lx
 return(data.frame(ages,n,ax,mx,qx,px,lx,dx,Lx,Tx,ex))
}

