# making lifetable from lx
lifetable3 <- function (lx, ax=0.5, n=1) {
    nc <- length(lx)
    if (length(ax) == 1) {
        ax <- rep(ax, nc)
    }
    if (length(n) == 1) {
        n <- rep(n, nc)
    }
    ages <- c(0, cumsum(n)[1:(nc - 1)])
    mx <- qx <- px <- dx <- Lx <- numeric(nc)
    for (i in 1:(nc - 1)) {
        dx[i] <- lx[i] - lx[i+1]
        qx[i] <- dx[i]/lx[i]
        px[i] <- 1 - qx[i]
        mx[i] <- qx[i]/(n[i] * (1 - qx[i] * (1 - ax[i])))
        Lx[i] <- n[i] * (lx[i+1] + ax[i] * dx[i])
    }
    dx[nc] <- lx[nc]
    qx[nc] <- 1
    px[nc] <- 0
    mx[nc] <- 1/(n[nc] * ax[nc])
    Lx[nc] <- lx[nc]/mx[nc]
    Tx <- rev(cumsum(rev(Lx)))
    ex <- Tx/lx
    return(data.frame(ages, n, ax, mx, qx, px, lx, dx, Lx, Tx, ex))
}

# making lifetable from qx
clifetable <- function(qx) {
 nc <- length(qx)
 lx <- numeric(nc)
 dx <- numeric(nc)
 Lx <- numeric(nc)
 lx[1] <- 100000
 for (i in 1:(nc-1)) {
  dx[i] <- lx[i]*qx[i]
  lx[i+1] <- lx[i]-dx[i]
  Lx[i] <- (lx[i]+lx[i+1])/2
 }
 dx[nc] <- lx[nc]
 Lx[nc] <- lx[nc]/2 # Wada (2006)
 Tx <- rev(cumsum(rev(Lx)))
 ex <- Tx/lx
 return(data.frame(qx,lx,dx,Lx,Tx,ex))
}

